package zhiyuan.com.loan.activity;import android.app.Activity;import android.content.Intent;import android.graphics.BitmapFactory;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.WindowManager;import android.widget.ImageView;import com.lidroid.xutils.BitmapUtils;import zhiyuan.com.loan.R;public class ChatImageActivity extends BaseActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN, WindowManager.LayoutParams.FLAG_FULLSCREEN);        setContentView(R.layout.activity_chat_image);        initView();    }    private void initView() {        ImageView iv_chatimage_iamge = (ImageView) findViewById(R.id.iv_chatimage_iamge);        Intent intent = getIntent();        String localIconUrl = intent.getStringExtra("localIconUrl");        if (!localIconUrl.startsWith("https")){            if (iv_chatimage_iamge != null) {                iv_chatimage_iamge.setImageBitmap(BitmapFactory.decodeFile(localIconUrl));            }        }        else {            BitmapUtils bitmapUtils = new BitmapUtils(this);            bitmapUtils.display(iv_chatimage_iamge, localIconUrl);        }    }}